// These functions must reside in this folder to ensure that the 'isHardhatFile' function in the 'anonymizer' file returns true.
// This is because the file path must contain 'node_modules/@nomicfoundation'.

import { ErrorDescriptor, HardhatError } from "@nomicfoundation/hardhat-errors";

const MOCK_ERROR_DESCRIPTOR: ErrorDescriptor = {
  number: 111111111111,
  messageTemplate: "test-error",
  websiteTitle: "test-error",
  websiteDescription: "test-error",
};

export const ERROR: Error = new Error("test-error");

export const HARDHAT_ERROR: HardhatError = new HardhatError({
  ...MOCK_ERROR_DESCRIPTOR,
  shouldBeReported: true,
});
